import React, { useState, useEffect, useRef, useCallback } from 'react';
import styled from "styled-components"
import { LeftSquareOutlined } from '@ant-design/icons';

const Calculator = () => {

    const [numbersDisplay, setNumbersDisplay] = useState("");

    const [ numbersBefore, setNumbersBefore] = useState("");

    const [ numbersAfter, setNumbersAfter] = useState("");

    const [ results, setResults] = useState(null);

    const currentOperation = useRef("");

    const currentNumberBefore = useRef("");

    const currentNumberAfter = useRef("");

    console.log(currentNumberBefore.current)
    console.log(currentNumberAfter.current)


    const handleKeyDown = (e) => {
        let key = e.key;
        if(e.shiftKey && key === "*") {
            key = "*"
        }
        if(e.shiftKey && key === "%") {
            key = "%"
        }
        const reg = new RegExp(/^[0-9]+$/);
        if(key === "+" || key === "-" || key === "*" || key === "/" || key === "%") {
            currentOperation.current = key
            setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${key}`))
        }
        if(key.match(reg) !== null) {
            const number = Number(key);
            setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${number}`))
            if(currentOperation.current === "") {
                setNumbersBefore((numbersBefore) => +`${numbersBefore}${number}`)
            } else {
                setNumbersAfter((numbersAfter) => +`${numbersAfter}${number}`) 
            }
        }
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
    }, []);


    const clear = () => {
        setNumbersDisplay(() => "")
        setNumbersBefore(() => "")
        setNumbersAfter(() => "")
        setResults(() => "")
        currentOperation.current = "";
    }

    const clickOperation = (e) => {
        setNumbersDisplay((prev) => `${prev}${e.target.innerText}`)
        if(currentOperation.current === '') {
            currentOperation.current = e.target.innerText;
        } else if (currentOperation.current !== '') {
            currentOperation.current = e.target.innerText;
            setNumbersBefore(() => results);
            setNumbersAfter(() => '');
            currentOperation.current = e.target.innerText;
        }
    }

    const getItem = (e) => {
        let getedItem = e.target.innerText
        if( +getedItem === 0 && (numbersDisplay === "" || numbersDisplay === 0 )) return
        if(currentOperation.current === "") {
            setNumbersBefore((numbersBefore) => toString(`${numbersBefore}${getedItem}`))
            currentNumberBefore.current = currentNumberBefore.current.concat(getedItem);
        } else {
            setNumbersAfter((numbersAfter) => toString(`${numbersAfter}${getedItem}`))
            currentNumberAfter.current = currentNumberAfter.current.concat(getedItem);
        }
        setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${getedItem}`))
    }


    const getResulst = (currentNumberBefore, currentNumberAfter) => {
        //console.log(typeof Number(numbersBefore));
        //console.log(typeof Number(numbersAfter));
        //const num1 = numbersBefore;
        //const num2 = numbersAfter;
        console.log(currentNumberBefore.current);
        console.log(currentNumberAfter.current);
        // const sum = (+numbersBefore) + (+numbersAfter);



        //switch(currentOperation.current){
        //    case "+":
        //        setResults(() => Number(numbersBefore) + Number(numbersAfter))
        //        break
        //    case "*":
        //        setResults(() => +numbersBefore * +numbersAfter)
        //        break
        //    case "/":
        //        setResults(() => +numbersBefore / +numbersAfter)
        //        break
        //    case "%":
        //        setResults(() => +numbersBefore % +numbersAfter)
        //        break
        //    case "-":
        //        setResults(() => +numbersBefore - +numbersAfter)
        //        break
        //        
        //    default:
        //        return currentOperation.current
        //}
       //if(results !== null) {
       //    const reverseResults = () => {
       //    currentOperation.current = "";
       //    setNumbersBefore(() => results)
       //    setNumbersAfter(() => "")
       //    setResults(() => null)
       //    setNumbersDisplay(() => results)
       //    }
       //    reverseResults()
       //}
    }



    useEffect(() => {
        if(results === Infinity || results === NaN) {
            alert("Never devide any numbers by zero, press OK and never do this")
            clear()
        }
    }, [results])

    return(
        <Wrapper>
            <AnswerBlock>
                <GetNumbers>
                    {numbersDisplay}
                </GetNumbers>
                <Answer>
                    {results}
                </Answer>
            </AnswerBlock>
            <FLexContainer>
                    <RowBlock>
                        <button style={{background: "#d0cccc"}} onClick={clear}>AC</button>
                        <button>
                            <LeftSquareOutlined style={{fontSize: "25px"}}/>
                        </button>
                        <button onClick={(e) => clickOperation(e)}>%</button>
                        <button onClick={(e) => clickOperation(e)}>/</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>7</button>
                        <button onClick={(e) => getItem(e)}>8</button>
                        <button onClick={(e) => getItem(e)}>9</button>
                        <button onClick={(e) => clickOperation(e)}>*</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>4</button>
                        <button onClick={(e) => getItem(e)}>5</button>
                        <button onClick={(e) => getItem(e)}>6</button>
                        <button onClick={(e) => clickOperation(e)}>-</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>1</button>
                        <button onClick={(e) => getItem(e)}>2</button>
                        <button onClick={(e) => getItem(e)}>3</button>
                        <button onClick={(e) => clickOperation(e)}>+</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>.</button>
                        <button onClick={(e) => getItem(e)}>0</button>
                        <ExceptionH3 onClick={() => getResulst(currentNumberBefore, currentNumberAfter)}>=</ExceptionH3>
                    </RowBlock>
                </FLexContainer>
        </Wrapper>
    );
}
const Wrapper = styled.div`
    width: 400px;
    height: 600px;
    background: #cee2f3;
    border: 0.5px solid black;
    padding: 25px;
`;
const AnswerBlock = styled.div`
    height: calc(100% / 5);
    width: 100%;
    background: white;
