import React, { useState, useEffect } from 'react';
import styled from "styled-components"
import { LeftSquareOutlined } from '@ant-design/icons';



const Calculator = () => {
    const [numbersDisplay, setNumbersDisplay] = useState("");

    const [ numbersBefore, setNumbersBefore] = useState("");

    const [ numbersAfter, setNumbersAfter] = useState("");

    const [ results, setResults] = useState("");

    const [operation, setOperation] = useState("")

    const clickOperation = (e) => {
        setOperation(e.target.innerText);
        getItem(e)
    }
    useEffect(() => {

    },[numbersAfter])

    const getItem = (e) => {
        setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${e.target.innerText}${operation}`))
        if(operation === "") {
            setNumbersBefore(() => +`${numbersBefore}${e.target.innerText}`)
        } else {
            setNumbersAfter(() => +`${numbersAfter}${e.target.innerText}`)
        }
    }

    const getResult = (operation) => {
        switch(operation){
            case "+":
                setResults(numbersBefore + numbersAfter)
                break
            default:
                return operation
        }

    }




   // const multiply = () => {
   //     setNumbersBefore(() => numbersDisplay)
   // }




    return(
        <Wrapper>
            <AnswerBlock>
                <GetNumbers>
                    {numbersDisplay}
                </GetNumbers>
                <Answer>
                    {results}
                </Answer>
            </AnswerBlock>
            <FLexContainer>
                    <RowBlock>
                        <button style={{background: "#d0cccc"}}>AC</button>
                        <button>
                            <LeftSquareOutlined style={{fontSize: "25px"}}/>
                        </button>
                        <button onClick={(e) => clickOperation(e)}>%</button>
                        <button onClick={(e) => clickOperation(e)}>/</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>7</button>
                        <button onClick={(e) => getItem(e)}>8</button>
                        <button onClick={(e) => getItem(e)}>9</button>
                        <button onClick={(e) => clickOperation(e)}>*</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>4</button>
                        <button onClick={(e) => getItem(e)}>5</button>
                        <button onClick={(e) => getItem(e)}>6</button>
                        <button onClick={(e) => clickOperation(e)}>-</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>1</button>
                        <button onClick={(e) => getItem(e)}>2</button>
                        <button onClick={(e) => getItem(e)}>3</button>
                        <button onClick={(e) => clickOperation(e)}>+</button>
                    </RowBlock>
                    <RowBlock>
                        <button>.</button>
                        <button onClick={(e) => getItem(e)}>0</button>
                        <ExceptionH3 onClick={() => getResult(operation)}>=</ExceptionH3>
                    </RowBlock>
                </FLexContainer>
        </Wrapper>
    );
}
const Wrapper = styled.div`
    position: relative;
    width: 400px;
    height: 600px;
    background: #cee2f3;
    border: 0.5px solid black;
    padding: 25px;
`;
const AnswerBlock = styled.div`
    height: calc(100% / 5);
    width: 100%;
    background: white;
    border: 0.5px solid black;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    justify-content: center;
    color: black;
`;
const GetNumbers = styled.div`
    width: calc(100% / 2);
    height: 40px;
    text-align: center;
    padding-top: 10px;
`;
const Answer = styled.div`
    width: calc(100% / 2);
    height: 40px;
    padding-top: 10px;
    text-align: right;
    padding-right: 15px;
`;

const FLexContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 10px;
`;
const RowBlock = styled.div`
    display: grid;
    grid-template-columns: repeat(4, calc(((400px / 4) - 21px)));
    margin-top: 25px;
    grid-column-gap: 10px;
    grid-auto-rows: 60px;

    & > *:last-child {
        border: 0.5px solid #fcb13e;
    }

    button {
        border: 1px solid black;
        background: white;
        text-align: center;
        cursor: pointer;
    }
`;
const ExceptionH3 = styled.button`
    border: 0.5px solid black !important;
    background: #ff9c04 !important;
`;
export default Calculator
