import React, { useState } from 'react';
import styled from "styled-components"
import { LeftSquareOutlined } from '@ant-design/icons';



const Calculator = () => {
    const [numbersDisplay, setNumbersDisplay] = useState("");
    const [ numbersBefore, setNumbersBefore] = useState("");
    const [ numbersAfter, setNumbersAfter] = useState("");
    const [ reply, setReply] = useState("");


    const getMathAction = (e) => {
        const mathAction = e.target.innerText;
        switch(mathAction) {
            case '*':
                multiply()
                    break
            default:
                return mathAction
         }
    }
    const multiply = () => {
        setReply(() => numbersBefore * numbersAfter)
    }


    const getItem = (e) => {
        setNumbersDisplay((prev) => (prev + e.target.innerText))
    }
    return(
        <Wrapper>
            <AnswerBlock>
                <GetNumbers>
                    {numbersDisplay}
                </GetNumbers>
                <Answer>
                    1
                </Answer>
            </AnswerBlock>
            <FLexContainer>
                    <RowBlock>
                        <button style={{background: "#d0cccc"}}>AC</button>
                        <button>
                            <LeftSquareOutlined style={{fontSize: "25px"}}/>
                        </button>
                        <button>%</button>
                        <button>/</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>7</button>
                        <button onClick={(e) => getItem(e)}>8</button>
                        <button onClick={(e) => getItem(e)}>9</button>
                        <button onClick={(e) => getMathAction(e)}>*</button>
                    </RowBlock>
                    <RowBlock>
                        <button>4</button>
                        <button>5</button>
                        <button>6</button>
                        <button>-</button>
                    </RowBlock>
                    <RowBlock>
                        <button>1</button>
                        <button>2</button>
                        <button>3</button>
                        <button>+</button>
                    </RowBlock>
                    <RowBlock>
                        <button>.</button>
                        <button>0</button>
                        <ExceptionH3>=</ExceptionH3>
                    </RowBlock>
                </FLexContainer>
        </Wrapper>
    );
}
const Wrapper = styled.div`
    position: relative;
    width: 400px;
    height: 600px;
    background: #cee2f3;
    border: 0.5px solid black;
    padding: 25px;
`;
const AnswerBlock = styled.div`
    height: calc(100% / 5);
    width: 100%;
    background: white;
    border: 0.5px solid black;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    justify-content: center;
    color: black;
`;
const GetNumbers = styled.div`
    width: calc(100% / 2);
    height: 40px;
    text-align: center;
    padding-top: 10px;
`;
const Answer = styled.div`
    width: calc(100% / 2);
    height: 40px;
    padding-top: 10px;
    text-align: right;
    padding-right: 15px;
`;

const FLexContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 10px;
`;
const RowBlock = styled.div`
    display: grid;
    grid-template-columns: repeat(4, calc(((400px / 4) - 21px)));
    margin-top: 25px;
    grid-column-gap: 10px;
    grid-auto-rows: 60px;

    & > *:last-child {
        border: 0.5px solid #fcb13e;
    }

    button {
        border: 1px solid black;
        background: white;
        text-align: center;
        cursor: pointer;
    }
`;
const ExceptionH3 = styled.button`
    border: 0.5px solid black !important;
    background: #ff9c04 !important;
`;
export default Calculator
