import React, { useState, useEffect, useRef } from 'react';
import styled from "styled-components"
import { LeftSquareOutlined } from '@ant-design/icons';

const Calculator = () => {

    const [numbersDisplay, setNumbersDisplay] = useState("");

    const [ numbersBefore, setNumbersBefore] = useState("");

    const [ numbersAfter, setNumbersAfter] = useState("");

    const [ results, setResults] = useState(null);

    const currentOperation = useRef("");

    const currentNumberBefore = useRef("");

    const currentNumberAfter = useRef("");

    const handleKeyDown = (e) => {
        let key = e.key;
        if(e.shiftKey && key === "*") {
            key = "*"
        }
        if(e.shiftKey && key === "%") {
            key = "%"
        }
        const reg = new RegExp(/^[0-9]+$/);
        if(key === "+" || key === "-" || key === "*" || key === "/" || key === "%") {
            currentOperation.current = key
            setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${key}`))
        }
        if(key.match(reg) !== null) {
            const number = Number(key);
            setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${number}`))
            if(currentOperation.current === "") {
                setNumbersBefore((numbersBefore) => +`${numbersBefore}${number}`)
            } else {
                setNumbersAfter((numbersAfter) => +`${numbersAfter}${number}`) 
            }
        }
    };

    useEffect(() => {
        window.addEventListener('keydown', handleKeyDown);
    }, []);

    useEffect(() => {
        console.log(currentNumberBefore.current);
        console.log(currentNumberAfter.current);
        console.log(results);
    }, [currentNumberBefore.current, currentNumberAfter.current, results]);

    const clear = () => {
        setNumbersDisplay(() => "")
        setNumbersBefore(() => "")
        setNumbersAfter(() => "")
        setResults(() => "")
        currentOperation.current = "";
        currentNumberBefore.current = "";
        currentNumberAfter.current = "";
    }

    const clickOperation = (e) => {
        setNumbersDisplay((prev) => `${prev}${e.target.innerText}`)
        if(currentOperation.current === '') {
            currentOperation.current = e.target.innerText;
        } else if (currentOperation.current !== '') {
            currentOperation.current = e.target.innerText;
            const res = (+currentNumberBefore.current) + (+currentNumberAfter.current);
            const strRes = res.toString()
            currentNumberBefore.current = strRes;
            currentNumberAfter.current = "";
            currentOperation.current = e.target.innerText;
        }
    }

    const getItem = (e) => {
        let getedItem = e.target.innerText
        if( +getedItem === 0 && (numbersDisplay === "" || numbersDisplay === 0 )) return
        if(currentOperation.current === "") {
            setNumbersBefore((numbersBefore) => toString(`${numbersBefore}${getedItem}`))
            currentNumberBefore.current = currentNumberBefore.current.concat(getedItem);
        } else {
            setNumbersAfter((numbersAfter) => toString(`${numbersAfter}${getedItem}`))
            currentNumberAfter.current = currentNumberAfter.current.concat(getedItem);
        }
        setNumbersDisplay((numbersDisplay) => (`${numbersDisplay}${getedItem}`))
    }


    const getResulst = () => {
        const prevNumber = Number(currentNumberBefore.current);
        const nextNumber = Number(currentNumberAfter.current);

        let isPointAppear = false;
        if(numbersDisplay) {
            isPointAppear = numbersDisplay.includes(".");
        }
        let toFixedCheckPoint = isPointAppear ? 1 : 0;

        switch(currentOperation.current){
           case "+":
                setResults(() => (prevNumber + nextNumber).toFixed(toFixedCheckPoint))
                break
           case "*":
                setResults(() => (prevNumber * nextNumber).toFixed(toFixedCheckPoint))
                break
           case "/":
                setResults(() => (prevNumber / nextNumber).toFixed(toFixedCheckPoint))
                break
           case "%":
                setResults(() => (prevNumber % nextNumber).toFixed(toFixedCheckPoint))
                break
           case "-":
                setResults(() => (prevNumber - nextNumber).toFixed(toFixedCheckPoint))
           default:
               return currentOperation.current
        }

        if(results !== null || results < -1) {
          const reverseResults = () => {
            let test = "+";
            let nstest = parseInt(test);
            console.log(nstest);
            const res = `${+currentNumberBefore.current} ${nstest} ${+currentNumberAfter.current}`;
            const strRes = res.toString();
            currentOperation.current = "";
            currentNumberBefore.current = strRes;
            currentNumberAfter.current = "";
            setResults(() => null)
            setNumbersDisplay(() => res)
          }
          reverseResults()
        }
    }  

    useEffect(() => {
        if(results === Infinity || results === NaN) {
            alert("Never devide any numbers by zero, press OK and never do this")
            clear()
        }
    }, [results])

    return(
        <Wrapper>
            <AnswerBlock>
                <GetNumbers>
                    {numbersDisplay}
                </GetNumbers>
                <Answer>
                    {results}
                </Answer>
            </AnswerBlock>
            <FLexContainer>
                    <RowBlock>
                        <button style={{background: "#d0cccc"}} onClick={clear}>AC</button>
                        <button>
                            <LeftSquareOutlined style={{fontSize: "25px"}}/>
                        </button>
                        <button onClick={(e) => clickOperation(e)}>%</button>
                        <button onClick={(e) => clickOperation(e)}>/</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>7</button>
                        <button onClick={(e) => getItem(e)}>8</button>
                        <button onClick={(e) => getItem(e)}>9</button>
                        <button onClick={(e) => clickOperation(e)}>*</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>4</button>
                        <button onClick={(e) => getItem(e)}>5</button>
                        <button onClick={(e) => getItem(e)}>6</button>
                        <button onClick={(e) => clickOperation(e)}>-</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>1</button>
                        <button onClick={(e) => getItem(e)}>2</button>
                        <button onClick={(e) => getItem(e)}>3</button>
                        <button onClick={(e) => clickOperation(e)}>+</button>
                    </RowBlock>
                    <RowBlock>
                        <button onClick={(e) => getItem(e)}>.</button>
                        <button onClick={(e) => getItem(e)}>0</button>
                        <ExceptionH3 onClick={getResulst}>=</ExceptionH3>
                    </RowBlock>
                </FLexContainer>
        </Wrapper>
    );
}
const Wrapper = styled.div`
    width: 400px;
    height: 600px;
    background: #cee2f3;
    border: 0.5px solid black;
    padding: 25px;
`;
const AnswerBlock = styled.div`
    height: calc(100% / 5);
    width: 100%;
    background: white;
    border: 0.5px solid black;
    display: flex;
    flex-direction: column;
    align-items: flex-end;
    justify-content: center;
    color: black;
`;
const GetNumbers = styled.div`
    width: calc(100% / 2);
    height: 40px;
    text-align: center;
    padding-top: 10px;
`;
const Answer = styled.div`
    width: calc(100% / 2);
    height: 40px;
    padding-top: 10px;
    text-align: right;
    padding-right: 15px;
`;

const FLexContainer = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    margin-top: 10px;
`;
const RowBlock = styled.div`
    display: grid;
    grid-template-columns: repeat(4, calc(((400px / 4) - 21px)));
    margin-top: 25px;
    grid-column-gap: 10px;
    grid-auto-rows: 60px;

    & > *:last-child {
        border: 0.5px solid #fcb13e;
    }

    button {
        border: 1px solid black;
        background: white;
        text-align: center;
        cursor: pointer;
    }
`;
const ExceptionH3 = styled.button`
    border: 0.5px solid black !important;
    background: #ff9c04 !important;
`;
export default Calculator;
